import {
  AppBar,Toolbar,Typography,Button,makeStyles,} from "@material-ui/core";
import { useHistory } from "react-router-dom";

import isAuth, { userType } from "../lib/isAuth";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

const Navbar = (props) => {
  const classes = useStyles();
  let history = useHistory();

  const handleClick = (location) => {
    console.log(location);
    history.push(location);
  };

  return (
    <AppBar position="fixed" style={{ backgroundColor: '#225f7e' }}>
      <Toolbar style={{ minHeight: "80px" }}>
        <Typography variant="h6" className={classes.title}>
          <img src="logo1.ico" width="30px" height="auto" alt="Logo"></img> JOB HARBOR
        </Typography>
        <div className="nav-links">
          {isAuth() ? (
            userType() === "recruiter" ? (
              <>
                <Button
                  color="inherit"
                  onClick={() => handleClick("/home")}
                >
                  Home
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/addjob")}
                >
                  Add Jobs
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/myjobs")}
                >
                  Posted
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/employees")}
                >
                  Employees
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/profile")}
                >
                  Profile
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/logout")}
                >
                  Logout
                </Button>
              </>
            ) : (
              <>
                <Button
                  color="inherit"
                  onClick={() => handleClick("/home")}
                >
                  Home
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/applications")}
                >
                  Applied
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/profile")}
                >
                  Profile
                </Button>

                <Button
                  color="inherit"
                  onClick={() => handleClick("/logout")}
                >
                  Logout
                </Button>
              </>
            )
          ) : (
            <>
              <Button
                color="inherit"
                onClick={() => handleClick("/login")}
              >
                Login
              </Button>

              <Button
                color="inherit"
                onClick={() => handleClick("/signup")}
              >
                SignUp
              </Button>
            </>
          )}
        </div>
      </Toolbar>
    </AppBar>
  );
};
