import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Modal from "@material-ui/core/Modal";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Slider from "@material-ui/core/Slider";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";
import FilterListIcon from "@material-ui/icons/FilterList";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
 paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
 },
}));

const FilterPopup = ({ open, handleClose, searchOptions, setSearchOptions }) => {
 const classes = useStyles();

 const handleJobTypeChange = (event) => {
    setSearchOptions({
      ...searchOptions,
      jobType: {
        ...searchOptions.jobType,
        [event.target.name]: event.target.checked,
      },
    });
 };

 const handleSliderChange = (event, newValue) => {
    setSearchOptions({
      ...searchOptions,
      salary: newValue,
    });
 };

 const handleSortChange = (event) => {
    setSearchOptions({
      ...searchOptions,
      sort: {
        ...searchOptions.sort,
        [event.target.name]: {
          ...searchOptions.sort[event.target.name],
          direction: event.target.checked ? "asc" : "desc",
        },
      },
    });
 };

 return (
   <Modal
     open={open}
     onClose={handleClose}
     aria-labelledby="filter-modal-title"
     aria-describedby="filter-modal-description"
   >
     <Paper className={classes.paper}>
       <Typography variant="h6" id="filter-modal-title">
         Filters
       </Typography>
       <Grid container item direction="column" alignItems="flex-start">
         <Typography variant="body1">Job Type</Typography>
         <Grid container item direction="row" alignItems="center">
           <input
             type="checkbox"
             id="jobType1"
             name="fullTime"
             checked={searchOptions.jobType.fullTime}
             onChange={handleJobTypeChange}
           />
           <label htmlFor="jobType1">Full Time</label>
         </Grid>
         <Grid container item direction="row" alignItems="center">
           <input
             type="checkbox"
             id="jobType2"
             name="partTime"
             checked={searchOptions.jobType.partTime}
             onChange={handleJobTypeChange}
           />
           <label htmlFor="jobType2">Part Time</label>
         </Grid>
         <Typography variant="body1">Salary</Typography>
         <Grid container item direction="row" alignItems="center">
           <Slider
             value={searchOptions.salary}
             onChange={handleSliderChange}
             aria-labelledby="continuous-slider"
             min={0}
             max={100}
           />
           <Typography variant="body1">
             ${searchOptions.salary.toFixed(2)}
           </Typography>
         </Grid>
         <Typography variant="body1">Sort By</Typography>
         <Grid container item direction="row" alignItems="center">
           <input
             type="checkbox"
             id="sort1"
             name="date"
             checked={searchOptions.sort.date.direction === "asc"}
             onChange={handleSortChange}
           />
           <label htmlFor="sort1">Date</label>
           <IconButton>
             <ArrowUpwardIcon />
           </IconButton>
           <IconButton>
             <ArrowDownwardIcon />
           </IconButton>
         </Grid>
         <Grid container item direction="row" alignItems="center">
           <input
             type="checkbox"
             id="sort2"
             name="salary"
             checked={searchOptions.sort.salary.direction === "asc"}
             onChange={handleSortChange}
           />
           <label htmlFor="sort2">Salary</label>
           <IconButton>
             <ArrowUpwardIcon />
           </IconButton>
           <IconButton>
             <ArrowDownwardIcon />
           </IconButton>
         </Grid>
       </Grid>
       <Button
         variant="contained"
         color="primary"
         className={classes.button}
         startIcon={<SearchIcon />}
         onClick={handleClose}
       >
         Search
       </Button>
     </Paper>
   </Modal>
 );
};

export default FilterPopup;
