import React, { useState, useEffect } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  makeStyles,
  IconButton,
  Menu,
  MenuItem,
  Popover, // Tambahkan impor untuk Popover
} from "@material-ui/core";
import { Menu as MenuIcon } from "@material-ui/icons";
import { useHistory } from "react-router-dom";
import isAuth, { userType } from "../lib/isAuth";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

const Navbar = (props) => {
  const classes = useStyles();
  let history = useHistory();

  const [anchorEl, setAnchorEl] = useState(null);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const handleMenuClick = (location) => {
    history.push(location);
    handleClose();
  };

  const toggleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <AppBar position="fixed" style={{ backgroundColor: "#225f7e" }}>
      <Toolbar style={{ minHeight: "80px" }}>
        <Typography variant="h6" className={classes.title}>
          <img src="logo1.ico" width="30px" height="auto" alt="Logo" /> JOB HARBOR
        </Typography>
        {windowWidth >= 768 ? (
          <div className="nav-links">
            {/* ...Sisipkan tombol-tombol lainnya seperti sebelumnya */}
          </div>
        ) : (
          <IconButton
            edge="end"
            color="inherit"
            aria-label="menu"
            aria-controls="menu-appbar"
            aria-haspopup="true"
            onClick={toggleMenu}
            className="menu-icon"
          >
            <MenuIcon />
          </IconButton>
        )}
        <Popover
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleClose}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "right",
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
        >
          <div style={{ padding: "20px" }}>
            {/* Isi menu ke dalam Popover */}
            <MenuItem onClick={() => handleMenuClick("/home")}>Home</MenuItem>
            <MenuItem onClick={() => handleMenuClick("/addjob")}>Add Jobs</MenuItem>
            <MenuItem onClick={() => handleMenuClick("/myjobs")}>Posted</MenuItem>
            <MenuItem onClick={() => handleMenuClick("/employees")}>Employees</MenuItem>
            <MenuItem onClick={() => handleMenuClick("/profile")}>Profile</MenuItem>
            <MenuItem onClick={() => handleMenuClick("/logout")}>Logout</MenuItem>
          </div>
        </Popover>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
