import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  makeStyles,
  Popover, // Tambahkan impor untuk Popover
} from "@material-ui/core";
import { useHistory } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

const Navbar = (props) => {
  const classes = useStyles();
  let history = useHistory();

  const [anchorEl, setAnchorEl] = React.useState(null); // Tambahkan state untuk mengendalikan tampilan Popover

  const handleMenuClick = (location) => {
    history.push(location);
    setAnchorEl(null); // Tutup Popover setelah memilih menu
  };

  const togglePopover = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const open = Boolean(anchorEl);
  const id = open ? "simple-popover" : undefined;

  return (
    <AppBar position="fixed" style={{ backgroundColor: "#225f7e" }}>
      <Toolbar style={{ minHeight: "80px" }}>
        <Typography variant="h6" className={classes.title}>
          <img src="logo1.ico" width="30px" height="auto" alt="Logo" />
          JOB HARBOR
        </Typography>
        <div className="nav-links">
          {/* Tambahkan tombol untuk menampilkan Popover */}
          <Button
            onClick={togglePopover}
            className="menu-icon"
          >
            &#9776;
          </Button>
          {/* Isi menu ke dalam Popover */}
          <Popover
            id={id}
            open={open}
            anchorEl={anchorEl}
            onClose={() => setAnchorEl(null)}
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "center",
            }}
            transformOrigin={{
              vertical: "top",
              horizontal: "center",
            }}
          >
            <div style={{ width: "250px" }}>
              <MenuItem onClick={() => handleMenuClick("/home")}>Home</MenuItem>
              <MenuItem onClick={() => handleMenuClick("/addjob")}>Add Jobs</MenuItem>
              <MenuItem onClick={() => handleMenuClick("/myjobs")}>Posted</MenuItem>
              <MenuItem onClick={() => handleMenuClick("/employees")}>Employees</MenuItem>
              <MenuItem onClick={() => handleMenuClick("/profile")}>Profile</MenuItem>
              <MenuItem onClick={() => handleMenuClick("/logout")}>Logout</MenuItem>
            </div>
          </Popover>
        </div>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
