import { useState } from "react";
import {
  Button,
  Chip,
  Grid,
  IconButton,
  InputAdornment,
  makeStyles,
  Paper,
  TextField,
  Typography,
  Modal,
  Slider,
  FormControlLabel,
  MenuItem,
  Checkbox,
} from "@material-ui/core";
import Rating from "@material-ui/lab/Rating";
import SearchIcon from "@material-ui/icons/Search";
import FilterListIcon from "@material-ui/icons/FilterList";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";

const useStyles = makeStyles((theme) => ({
  body: {
    height: "inherit",
  },
  jobTileOuter: {
    padding: "30px",
    margin: "20px 0",
    boxSizing: "border-box",
    width: "100%",
  },
  popupDialog: {
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  statusBlock: {
    width: "100%",
    height: "15%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    textTransform: "uppercase",
  },
  search: {
    backgroundColor: "white",
    borderRadius: "12px",
    width: "100%",
    maxWidth: "500px", // Maksimum lebar untuk kolom pencarian
  },
  filterButton: {
    backgroundColor: "#7bc5d9", // Warna latar belakang tombol filter
  },
  title: {
    color: "white",
    fontWeight: "bold",
  },
}));

const MyJobs = () => {
  const classes = useStyles();
  const [filterOpen, setFilterOpen] = useState(false);
  const [searchOptions, setSearchOptions] = useState({
    query: "",
    jobType: {
      fullTime: false,
      partTime: false,
      wfh: false,
    },
    salary: [0, 100],
    duration: "0",
    sort: {
      salary: {
        status: false,
        desc: false,
      },
      duration: {
        status: false,
        desc: false,
      },
      rating: {
        status: false,
        desc: false,
      },
    },
  });

  return (
    <Grid
      container
      item
      direction="column"
      alignItems="center"
      style={{ padding: "30px", minHeight: "93vh" }}
    >
      <Grid
        item
        container
        direction="column"
        justify="center"
        alignItems="center"
      >
        <Grid item xs>
          <Typography variant="h4" className={classes.title}>
            My Jobs
          </Typography>
        </Grid>
        <Grid item xs>
          <TextField
            label="Search Jobs"
            value={searchOptions.query}
            onChange={(event) =>
              setSearchOptions({
                ...searchOptions,
                query: event.target.value,
              })
            }
            InputProps={{
              endAdornment: (
                <InputAdornment>
                  <IconButton>
                    <SearchIcon />
                  </IconButton>
                </InputAdornment>
              ),
            }}
            className={classes.search}
            variant="outlined"
          />
        </Grid>
        <Grid item>
          <IconButton
            onClick={() => setFilterOpen(true)}
            className={classes.filterButton}
          >
            <FilterListIcon />
          </IconButton>
        </Grid>
      </Grid>

      <Grid
        container
        item
        xs
        direction="column"
        alignItems="stretch"
        justify="center"
        spacing={2}
      >
        <Grid item xs>
          <JobTile job={{}} />
        </Grid>
      </Grid>
    </Grid>
  );
};

export default MyJobs;
