import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles({
 root: {
    minWidth: 275,
    maxWidth: 500,
    marginBottom: 10,
 },
 bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
 },
 title: {
    fontSize: 14,
 },
 pos: {
    marginBottom: 12,
 },
});

const Applications = () => {
 const classes = useStyles();
 const [applications, setApplications] = useState([
    { id: 1, name: "Application 1", status: "applied" },
    { id: 2, name: "Application 2", status: "rejected" },
    { id: 3, name: "Application 3", status: "accepted" },
 ]);
 const [open, setOpen] = useState(false);
 const [selectedApp, setSelectedApp] = useState(null);

 const handleClickOpen = (app) => {
    setSelectedApp(app);
    setOpen(true);
 };

 const handleClose = () => {
    setOpen(false);
 };

 const updateApplicationStatus = (id, status) => {
    const updatedApplications = applications.map((app) =>
      app.id === id ? { ...app, status } : app
    );
    setApplications(updatedApplications);
 };

 return (
    <div>
      {applications.map((app) => (
        <Card key={app.id} className={classes.root}>
          <CardContent>
            <Typography
              className={classes.title}
              color="textSecondary"
              gutterBottom
            >
              Job Application
            </Typography>
            <Typography variant="h5" component="h2">
              {app.name}
            </Typography>
            <Typography className={classes.pos} color="textSecondary">
              Status: {app.status}
            </Typography>
          </CardContent>
          <CardActions>
            <Button
              size="small"
              onClick={() => handleClickOpen(app)}
            >
              View
            </Button>
          </CardActions>
        </Card>
      ))}
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Application Details</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please update the application status.
          </DialogContentText>
          <form>
            <FormControl fullWidth>
              <InputLabel htmlFor="status">Status</InputLabel>
              <Select
                autoFocus
                value={selectedApp ? selectedApp.status : ""}
                onChange={(e) => {
                 updateApplicationStatus(selectedApp.id, e.target.value);
                }}
                inputProps={{
                 name: "status",
                 id: "status",
                }}
              >
                <MenuItem value="applied">Applied</MenuItem>
                <MenuItem value="rejected">Rejected</MenuItem>
                <MenuItem value="accepted">Accepted</MenuItem>
              </Select>
            </FormControl>
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
 );
};

export default Applications;
