import { useState } from "react";
import {
  Button,
  Grid,
  Typography,
  Paper,
  makeStyles,
  TextField,
  Avatar,
} from "@material-ui/core";
import ChipInput from "material-ui-chip-input";
import FileUploadInput from "../lib/FileUploadInput";
import DescriptionIcon from "@material-ui/icons/Description";
import FaceIcon from "@material-ui/icons/Face";

const useStyles = makeStyles((theme) => ({
  body: {
    height: "inherit",
  },
  avatar: {
    width: theme.spacing(17),
    height: theme.spacing(17),
  },
  inputBox: {
    marginBottom: theme.spacing(2),
  },
}));

const MultifieldInput = (props) => {
  const classes = useStyles();
  const { education, setEducation } = props;

  return (
    <>
      {education.map((obj, key) => (
        <Grid item container className={classes.inputBox} key={key}>
          <Grid item xs={12} sm={6}>
            <TextField label={`Institution Name`} variant="outlined" fullWidth />
          </Grid>
          <Grid item xs={6} sm={3}>
            <TextField
              label="Start Year"
              value={obj.startYear}
              variant="outlined"
              type="number"
              onChange={(event) => {
                const newEdu = [...education];
                newEdu[key].startYear = event.target.value;
                setEducation(newEdu);
              }}
            />
          </Grid>
          <Grid item xs={6} sm={3}>
            <TextField
              label="End Year"
              value={obj.endYear}
              variant="outlined"
              type="number"
              onChange={(event) => {
                const newEdu = [...education];
                newEdu[key].endYear = event.target.value;
                setEducation(newEdu);
              }}
            />
          </Grid>
        </Grid>
      ))}
      <Grid item style={{ alignSelf: "center" }}>
        <Button
          variant="contained"
          color="secondary"
          onClick={() =>
            setEducation([
              ...education,
              {
                institutionName: "",
                startYear: "",
                endYear: "",
              },
            ])
          }
          className={classes.inputBox}
        >
          Add another institution details
        </Button>
      </Grid>
    </>
  );
};

const Profile = () => {
  const classes = useStyles();
  const [profileDetails, setProfileDetails] = useState({
    name: "",
    education: [],
    skills: [],
    resume: "",
    profile: "",
  });

  const [education, setEducation] = useState([
    {
      institutionName: "",
      startYear: "",
      endYear: "",
    },
  ]);

  return (
    <>
      <Grid
        container
        item
        direction="column"
        alignItems="center"
        style={{ padding: "20px", minHeight: "93vh" }}
      >
        <Grid item xs={12} sm={8} md={6}>
          <Paper>
            <Grid container direction="column" alignItems="center">
              <Avatar
                src={`${profileDetails.profile}`}
                className={classes.avatar}
              />
              <Typography variant="h3" component="h2" style={{ color: "#3f51b5", fontWeight: "bold" }}>
                Profile
              </Typography>
              <TextField
                label="Name"
                value={profileDetails.name}
                className={classes.inputBox}
                variant="outlined"
                fullWidth
              />
              <MultifieldInput education={education} setEducation={setEducation} />
              <ChipInput
                className={classes.inputBox}
                label="Skills"
                variant="outlined"
                helperText="Press enter to add skills"
                value={profileDetails.skills}
                onAdd={(chip) =>
                  setProfileDetails({
                    ...profileDetails,
                    skills: [...profileDetails.skills, chip],
                  })
                }
                onDelete={(chip, index) => {
                  let skills = profileDetails.skills;
                  skills.splice(index, 1);
                  setProfileDetails({
                    ...profileDetails,
                    skills: skills,
                  });
                }}
                fullWidth
              />
              <FileUploadInput
                className={classes.inputBox}
                label="Resume (Images only)"
                icon={<DescriptionIcon />}
                identifier={"resume"}
              />
              <Button
                variant="contained"
                color="primary"
                style={{ padding: "10px 50px", marginTop: "20px", borderRadius: "8px" }}
              >
                Update Details
              </Button>
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </>
  );
};

export default Profile;
